package proto;

option optimize_for = LITE_RUNTIME;
option java_package = "com.aemobile.patti.room.bean";
option java_outer_classname = "PattiFootballRoomProto";

import "commonproto.proto";

//房间变量 
//gameid:int 当前正在进行的游戏 / 24小时内的比赛
//team1:String 第一个team
//team2:String 第二个team
//odds1:double 第一个team odds
//odds2:double 第二个team odds
//odds3:double 平局odds
//score1:int 第一个team计分 
//score2:int 第二个team计分
//time:String 当前时间 
//end:boolean //停止下注
//leaguematch:String //联赛名称
//leaguestage:String //联赛阶段(小组赛、淘汰赛等等)
//isdraw:boolean //是否能下平
//starttime:int //开始时间
//gamestate:String //比赛阶段
//teamnum1:int //team1编号
//teamnum2:int //team2编号
//pen1:int //team1点球分数
//pen2:int //team2点球分数 
//maxbet:long 单次最大下注

//------------CMD winnerbet 下注
// C2S_WinnerBet //下平队伍名称传draw
//success / error / winnerbetError下注失败

//------------CMD winnerbetError下注失败
// S2C_WinnerBetError
//success / error

//------------CMD footballbets 下注记录
message C2S_Football_Match{
	optional Common_LS winIds = 1;//不需要的winner 的betId
}
message S2C_Football_MatchList{
	repeated S2C_Football_Match game = 1;
}

// 过去的比赛，需要知道参与双方
message S2C_Football_Match {
	required int32 gameid = 1; // gameId
	required string team1 = 2; //队伍名称
	required string team2 = 3; //队伍名称
	required int32 teamnum1 = 4; //队伍编号
	required int32 teamnum2 = 5; //队伍编号
	repeated S2C_WinnerBet winnerbets = 6;
	optional bool isdraw = 7;  //是否平局
}

// S2C_WinnerBet

//------------CMD footballbetresult 推送消息 下注结算
message S2C_Football_BetsList {
	repeated S2C_Football_Bets betslist = 1;
}
message S2C_Football_Bets{
	repeated S2C_WinnerBet winnerbets = 1;
	optional string team1 = 2;
	optional string team2 = 3;
	optional int32 teamnum1 = 4;
	optional int32 teamnum2 = 5;
}

//-------------CMD footballclaimbets
message C2S_Football_ClaimBet{
	required Common_LS winnerbet = 1;//winnner结算bets
}

//------------CMD footballlist 比赛列表
message S2C_FootballList {
	repeated S2C_Football football = 1;
}

message S2C_Football {
	required string roomname = 1; // roomname
	required string team1 = 2; //队伍名称
	required string team2 = 3; //队伍名称
	required int32 teamnum1 = 4; //队伍编号
	required int32 teamnum2 = 5; //队伍编号
	required int32 starttime = 6;//比赛开始时间
	required int32 state = 7; //比赛状态(1:比赛中 2:24小时内 3:24小时外)
}
