package proto;

option optimize_for = LITE_RUNTIME;
option java_package = "com.aemobile.patti.room.bean";
option java_outer_classname = "PattiCricketRoomProto";

import "commonproto.proto";

//------------加入房间
//JoinRoomRequest("Cricket") ); 
//SFSEvent.ROOM_JOIN / SFSEvent.ROOM_JOIN_ERROR

//房间变量 
//gameid:int 当前正在进行的游戏 / 24小时内的比赛
//--------- 仅当有当前比赛时
//team:String 当前正在进行比赛的team
//team1:String 第一个team
//team2:String 第二个team
//odds1:double 第一个team odds
//odds2:double 第二个team odds
//score1:String 第一个team计分 e.g 120/5  (1.2)
//score2:String 第二个team计分 e.g 120/5  (1.2)
//session:int 当前可下注session (0=session 不可下;1=6overs;2=10overs;3=15overs;4=20overs;5=第二局6overs)
//socre:预计分数
//socrecheck:预计分数校验码)
//gamesnum:String 赛事的第几场比赛
//maxbet:long 单次最大下注
//starttime:int //开始时间

//------------CMD winnerbet 下注
// C2S_WinnerBet
//success / error / winnerbetError下注失败

//------------CMD winnerbetError下注失败
// S2C_WinnerBetError

//------------CMD sessionbet 下注
message C2S_SessionBet{
	required SessionBet bet = 1;
	required int32 gameid = 2; // gameId
	required string socrecheck =3; // 预计分数校验码
}

message SessionBet{	
	required int32 amount = 1; // 下注数
	required int32 sessionid = 2; // Session
	required int32 score = 3; // 分数	
	required bool over = 4; //true=大于等于;false=小于
	required string team = 5;//队伍名称
}
//success / error

//------------CMD bets 下注记录
message C2S_Match{
	optional Common_LS winIds = 1;//不需要的winner 的betId
	optional Common_LS sessionIds = 2;//不需要的session 的betId
}
message S2C_MatchList{
	repeated S2C_Match game = 1;
}

// 过去的比赛，需要知道参与双方
message S2C_Match {
	required int32 gameid = 1; // gameId
	required string team1 = 2; //队伍名称
	required string team2 = 3; //队伍名称
	repeated S2C_SessionBet sessionbets = 4; 
	repeated S2C_WinnerBet winnerbets = 5;
}

// S2C_WinnerBet

message S2C_SessionBet{
	required int64 betid = 1; // betId
	required SessionBet bet = 2; 	
	required int32 createtime = 3; //下注时间
	required int32 result = 4; //结算（1输2赢0未结束）
	required int32 score = 5; //实际score
}

//------------CMD betresult 推送消息 下注结算
message S2C_Bets{
	repeated S2C_SessionBet sessionbets = 1; 
	repeated S2C_WinnerBet winnerbets = 2;
	optional string team1 = 3;
	optional string team2 = 4;
}

//-------------CMD claimbets
message C2S_ClaimBet{
	required Common_LS sessionbet = 1;//session结算bets
	required Common_LS winnerbet = 2;//winnner结算bets
}

//------------CMD newcricketlist 比赛列表
message S2C_CricketList {
	repeated S2C_Cricket cricket = 1;
}
