package proto;

option optimize_for = LITE_RUNTIME;
option java_package = "com.aemobile.patti.room.bean";
option java_outer_classname = "PattiWarRoomProto";

import "roomproto.proto";
import "commonproto.proto";

//和以前合用接口
//starttime

//Room Variable
//bi:long:banker user id
//po:int:pool
//bbi:long:be banker user id
//ba:int:bootAmount
//ra:int :rate
//j:long:jackpot
//bc:long:banker chips 上庄最小chips
//sc:long:sit chips 上桌最小chips
//mp:long:min pool 最小奖池
//la:long:land 下注金额（地）
//wa:long:water  下注金额（水）
//fi:long:fire 下注金额（火）
//wi:long:wind  下注金额（风）

//----------------CMD sitebet  在位子下注
message C2S_Site_Bet{
	required int32 site = 1;//1、地  2、水  3、火  4、风
	required int64 chips = 2;
}
message S2C_Site_Bet{
	required int32 site = 1;
	required int64 chips = 2;
	required int64 userId = 3;
}
//----------------CMD bebanker 成为庄家
//Common_L 选择的奖池
//Common_Boolean 如果没办法成为就返回false

//------------------CMD changepot 修改奖池
//Common_L 选择的奖池

//---------------CMD dividejackpot 分奖池（主动）
//S2C_Count

//---------------CMD count 结算（主动）
message S2C_Count{
	required int64 chips = 1;//自己赢的
	repeated Count count = 2;
}


//---------------CMD usercards  发牌(主动)

message S2C_User_Cards{
	repeated User_Card cards =1;
}
message User_Card{
	required int32 site = 1;
	repeated int32 cards = 2;
	optional bool win = 3;//是否胜利，除庄家外
	optional int64 chips = 4;//获胜金额
	repeated Count count = 5;
}

message Count{
	required int64 userId = 1;
	required int64 chips = 2;//别人赢的
}
//--------------CMD cancelbanker 下庄
//Common_I  1:庄家 2：预备庄家
//Common_Boolean

//-----------------CMD startpw
message Start_Patti_War{
	required int32 downTime = 1;
        repeated int32 chipsNum = 2;//筹码个数
}
