package proto;

option optimize_for = LITE_RUNTIME;
option java_package = "com.aemobile.gin.zone.bean";
option java_outer_classname = "GinZoneProto";

import "commonproto.proto";


//------------CMD keepconnection

//user variable
//vip 变化表示升级


//登陆------------CMD Login
//##
message C2S_Login {
    required bool isaccount=1; //是否账号登录 false=快速登录
    required string type=2;  //账号类型 (guest，facebook)
    required string deviceid=3;  //设备号
    optional int64 appid=4;// APP 编号
    optional string displayname=5; //用户显示名（facebook登录)
    optional string email=6; //邮箱（facebook登录)
    required int32 platform=7; //客户端平台 （3=android）
    required string version=8; //版本号
    required int32 release=9; //数字版本号
    optional string source=10; //渠道
    optional string lan=11;//语言
    optional string timezone=12; //时区
    optional string registerInfo=13; //渠道
    optional string device=14; //设备号
}
//##
message S2C_Login{
    required bool isbind = 1;//是否绑定
    optional bool isnew = 2;//是否是新用户
}

//facebook用户登录后，判断是否被邀请，如果被邀请，自动成为好友


//系统消息，------CMD systemmessage
message S2C_SystemMessage{
    optional string title=1; //标题
    optional string content=2; //内容
    optional bool continueplay=3; //是否可以继续游戏
    optional int32 messageframetype=4; //消息框类型
    optional int32 styletype=5;//展示类型 1表示smartfox内置广播，2表示弹框
    optional string oklink=6;//确定按钮链接
}

//------------CMD editusericon
//C2S_Common_I头像id/-1(使用facebook头像)

//-------------CMD cacheusericon
//Common_S facebook头像地址  （防止小游戏没有头像）
//------------CMD signin
//##Common_Booleans 是否签到， 是否看广告
message S2C_Signin {
    optional int32 continues_day = 1; //连续签到天数
    optional int32 signinremain = 2; //距离下一次签到时间
    optional S2C_Common_Change chips = 3; //金币变化 （仅签到时有效）
    optional S2C_SpinValue payedspin = 4; 
}

//------------CMD spinvalue
message S2C_SpinValue {
    required int32 coins = 1; //基础coins奖励
    required Common_IS times = 2; //倍数
    repeated int32 spincoins = 3; //基础coins值
    repeated Common_IS spintimes = 4; //倍数值
}

//------------CMD spin
//message S2C_Signin 

//------------CMD payedspin
//S2C_SpinValue

//-----------CMD advnum
//无参
//Common_I  广告观看剩余次数

//-----------CMD advchips
//无参
message S2C_AdvChips{
    required int32 add_chips = 1;
    required int64 final_chips = 2;
}

//----------CMD broken 破产奖励
//无参
//Common_I 奖励

//----------CMD acceptbroken 领取破产奖励
//Common_I 奖励
// 无返回


//-----------CMD registerInfo
//Common_S


//------CMD summary
//Common_S roomName
message S2C_Summary{
    message Sumr{
        required string name = 1;
        required int32 hands = 2;
        required int32 handsWin = 3;
        required int64 score = 4;
    }
    repeated Sumr sumr = 1;
}
//------CMD rounds
//Common_S roomName
message S2C_Rounds{
    message Roundr{
        required int32 round = 1;
        required int32 icon = 2;
        required string name = 3;
        required int64 score = 4;
    }
    repeated Roundr roundr = 1;
}
//User Variable
//idg:bool:isDegree  是否开启牌面等级计算

//---------------------------------------------------------------social----------------------------------------------
//------------CMD friendlist
//Common_Boolean 是否通知好友上线（登陆后第一次调用true,其他false）
message S2C_7_Friends {//在release为7之后
    repeated S2C_7_Friend  onlinefriend = 1; //在线好友列表
    repeated S2C_7_Friend  offlinefriend = 2; //离线好友列表
    message S2C_7_Friend {
        required S2C_Fri_UserInfo userinfo = 1; //基本信息
        optional string roomName = 2; //好友所在房间（仅限公共房间）后缀:# +（fri:friend,pri:private,tou:tournament）
    }
}

//------------CMD friendlistwithcoll
//Common_I 收藏品ID
message S2C_Coll_Friends {//在release为7之后
    repeated S2C_Fri_UserInfo  friends = 1;
}





//-----------CMD invitefbfriend
//Common_SS

//-----------CMD addfbfriends
//Common_SS

//-----------CMD friendonline
//S2C_Common_UserInfo

//------------CMD customerSerList 客服消息，定时请求，客户端保存聊天记录
//Common_I  //最新一条id

message S2C_CSL {
    required int32 lastId = 1;//最新一条id
    repeated S2C_CustomerSerChat chat = 2;//消息内容

    message S2C_CustomerSerChat {
        required int32 time = 1; //时间戳
        required bool isreply = 2; //1客服回复 0用户
        required string content = 3; //内容
        required int32 id = 4; //
    }
}
message Add_Friend{
    required int32 type = 1;//1:回应对象，2：自己好友人数限制失败 3：被请求者好友人数限制
    required int64 num = 2;//type为1：为用户ID，type为2，3：限制的人数
    optional S2C_Common_UserInfo userInfo = 3;
}
//---------------------CMD addFriReq 申请加好友
//Common_L 发送：要加谁为好友
//Add_Friend 接收：谁请求添加好友

//---------------------CMD addFriDec 加好友决定
//Common_L 发送：加谁为好友
//Add_Friend 接收：谁同意了请求

//---------------------CMD delFri 删除好友 (仅限游戏中加好友)
//Common_L
//Add_Friend 删除成功

//-----------CMD customerSerSendChat 发送消息，注意长度
message C2S_SendChat {
    required string message = 1;//消息内容
    required int32 messageId = 2;//发送消息的id
}

message S2C_SendChat {
    required int32 lastId = 1;//最新一条id 返回最新的id，是0的话就表示失败
    required int32 messageId = 2;//发送消息的id
}


//------------CMD notificationlist
//无参
message S2C_Notificaitons {
    repeated S2C_Notificaiton notifications = 1;//通知
    required int32 chipsget = 2; //chips领取次数

    message S2C_Notificaiton {
        required int64 id = 1; //请求ID
        required int32 type = 2; //类型 0=奖励发放 1=纯消息 2= 收藏品请求 3=收藏品赠送 4=chip赠送 5=钻石奖励发放 6=auto sort奖励发放 7=priavte奖励发放 8=记牌器奖励发放 9=suit奖励发放 10=某一个书册中具体的某一张牌
        required string title = 3; //title / 请求者名字
        required string message = 4; //message / 请求者头像 类型11 为多类型组合：0=100；5=100；6=3;...
        required string facebookid = 9; //取facebook用户头像
        optional int32 amount = 5; //获得金币 / 收藏品ID
        optional int64 amountL= 10; // 请求者ID
        required int32 senddate= 6; //发送时间
        required int32 expdate= 7; //过期时间
        required bool isread = 8; //是否已领取/已读
    }
}

//------------CMD myprofile
message S2C_MP{
    required int32 games = 1;//游戏总局数
    required int32 won = 2;//胜利数
    required int64 bwin = 3;//最高赢钱数
    required int32 winstreak = 4;//连赢数
    repeated int32 besthand = 5;//classic最大牌面
    required int64 hwin = 6;//最大金币数
    optional S2C_Common_LevelBonus levelbonus = 7;
    required int32 createDate = 8;//注册日期
}

//------------CMD acceptnotification 已读/领取
//Common_LS notification id list
message S2C_Notification_Result {
    repeated int64 is = 1; //请求ID
    required int32 chipsget = 5; //chips领取次数
    optional S2C_Common_Change chips = 2;//金币变化
    optional S2C_Common_Change diamond = 3;//diamond变化
    repeated S2C_Common_Change coll = 4;//收藏品 + 收藏品数量
}

//------------CMD acceptcollRequst
//Common_L notification id
//Common_LS id + 收藏品 + 收藏品数量

//------------deletenotification
//Common_LS notification id list
//success/error

//------------CMD deletecollRequst
//Common_L notification id
//success/error

//-------------invitecode 邀请码详情
//无参
message S2C_Invite_Code{
    required int32 num = 1; //邀请码被使用次数
    required string code = 2;//邀请码
    repeated int32 bonuses = 3;//邀请达到一定数目奖励金额
    required int32 accpeted = 4;//领取邀请数目奖励次数
    required bool isCode = 5;//是否可领取
    required bool hasInvited = 6;//有没有用户使用过邀请码
    repeated int32 bounsNum = 7;//到达奖励数目奖励的数目
    optional int32 codeDays = 8;//邀请码使用剩余天数
    required int32 codeBonus = 9;//使用邀请码能获得的奖励
    optional int32 invitedNum = 10;//目前使用邀请码但未领取的次数
}

//-------------invitedbonus 邀请码使用
//Common_S   邀请码
message S2C_Invited_Bonus{
    required S2C_Common_Change change =1 ;//金额变化
    required string name = 2;//使用了谁的邀请码
}

//------------ invitebonus 邀请获取的奖励
//Common_I  1:一个人奖励，2：到达一定数量奖励
message S2C_Invite_Bonus{
    required S2C_Common_Change change =1 ;//金额变化
    required int32 type = 2;
    optional string name = 3;//使用邀请码的用户昵称
    optional bool continue = 4;//是否可再领
    optional int32 accpeted = 5;//领取邀请数目奖励次数
}
