//--------------CMD promotelist
message S2C_PromoteList {
    repeated S2C_PromoteInfo promotes = 1; //促销活动列表
}

message S2C_PromoteInfo {
    optional int32 id = 1; //促销活动ID
    optional string name = 2; //促销活动名字
    optional int32 time = 3; //活动结束时间
    optional string uiConfig = 4; //UI配置
    optional string ruleConfig = 5; //客户端规则配置
    optional bool isWindow = 6; //是否弹窗
    optional string icon = 7; //icon内容
    optional int32 weight = 8; //促销权重
    repeated S2C_PromoteGoods goods = 9; //促销相关商品
}

message S2C_PromoteGoods {
    optional int32 id = 1; //促销活动商品索引
    optional string product_id = 2; // 商品id
    optional int32 totalCount = 4; //总共可以购买次数
    optional int32 buyCount = 5; //当前用户已购买次数
}
//------------CMD androidSubsBuy 安卓订阅购买
message C2S_Subs_Buy{
    repeated C2S_Android_Buy order = 1; //订单
}
//S2C_Subs_Profile
//cmd subsUserList
//--------------------CMD subsUserList
message SubsMsg{
    repeated Subs subs = 1;
    message Subs {
        required int64 userId = 1;
        required string orderId = 2;
        required bool isAuto = 3;//是否自动续费
        required int32 renewTime = 4;//倒计时
    }
}
//------------CMD subsProfile 订阅信息
//无参
message S2C_Subs_Profile{
    required int32 type = 1;//0：无，1：初级 ，2：高级
    optional bool isAccept = 2;//true:可以领取，false:已领取
    optional int32 chips = 3;//领取值
    optional int32 nextTime = 4;//下次领取奖励时间
    optional int32 renewTime = 5;//续费时间
    optional int64 allAccept = 6;//当前订阅周期领取的总数
    optional string orderId = 7;//当前订单id
    optional int32 nextType = 8;//下一个订单的月卡等级
    optional int32 nextStart = 9;//下一个周期订单的开始时间
    optional bool isAuto = 10;//是否自动续费
}
//-----------CMD subsAccept  领取
//无参
//S2C_Subs_Profile


//-----------CMD chipsfreeze  账户冻结
message C2S_Chips_Freeze{
    repeated Chips_Change change = 1;
}
message Chips_Change{
    required int64 userId = 1;
    required int64 chips = 2;
    required int64 finalChips = 3;
    required int32 type = 4;//1.冻结 2.清除 3.解冻
}


//----------------------------------------------ALL-------------------------------------------
//----------------CMD  getZoneMessage  ginofflineai/exhangelist/promotelist
// notificationlist/collectiongrouplist/iconlist/cardbacklist/sceneslist/gineffectlist/onlinebonus/advnum/broken/halloween/gintour/vipconfig/thankgiving/collectionsrate
//Common_SS 需要请求的接口
message S2C_Zone_Message{
    optional string ginofflineai = 1;//ginofflineai
    optional S2C_Diamond_Exchanges exhangelist = 2;//exhangelist
    optional S2C_PromoteList promotelist = 3;//promotelist
    optional S2C_Notificaitons notificationlist = 4;//notificationlist
    optional S2C_Collection_Groups collectiongrouplist = 5;//collectiongrouplist
    optional S2C_Icons iconlist = 6;//iconlist
    optional S2C_Cardbacks cardbacklist = 7;//cardbacklist
    optional S2C_Scenes sceneslist = 8;//sceneslist
    optional S2C_GinEffects gineffectlist = 9;//gineffectlist
    optional S2C_OnlineBonus onlinebonus = 10;//onlinebonus
    optional int32 advnum = 11;//advnum
    optional int32 broken = 12;//broken
    optional Common_IS halloween = 13;//halloween
    optional S2C_Gin_Tour gintour = 14;//gintour
    optional S2C_VIPS vipconfig = 15;//vipconfig
    optional Common_IS thankgiving = 16;//thankgiving
    optional S2C_CollectionRates collectionsrate = 17;//collectionsrate
}

//////////////gin rummy
//---------------------------------------------------jackpot------------------------------------------
message JackpotMessage{
    required int64 pool = 1;
    required int64 minus = 2;//积累要扣除的chips
    optional string name = 3; //奖池名称
    required string mode = 4;//包含的房间
}
//------------CMD jackpot 查看奖池信息
message C2S_Jackpot{
    required int32 level = 1;
    required string gmode = 2;//G模式
    required string rmode = 3;//R模式
    required string roomName = 4;
    optional string name = 5;
}
//JackpotMessage

//-----------CMD changejackpot 修改奖池金额
message C2S_Change_Jackpot{
    required C2S_Jackpot jackpot = 1;
    required int64 chips = 2;//修改的金额
    required int64 minusChips = 3;//修改的待回收金额
    optional string name = 4;
}
//message JackpotMessage

//------------CMD alljackpot 查看奖池信息(全部)
message S2C_ALL_Jackpot{
    repeated JackpotMessage jackpot = 1;
}

//-----------CMD changejackpots 修改奖池金额(批量)
message C2S_Change_ALL_Jackpot{
    repeated JackpotMessage jackpot = 1;
}
//message S2C_ALL_Jackpot


//------------CMD ginrummylevels  ginrummy等级
//gin模式(1普通)#房间模式(1classic)#赌注范围下限#赌注范围上限#gift#tip#需参与人数#房间等级#chips下限#chips上限#rtp#ginbonus#undercutbonus;
//无参
message S2C_Pool_RummyList{
    repeated S2C_Gin_Rummy leves = 1;
    message S2C_Gin_Rummy{
        required int32 gtype=1;//gin模式 1普通
        required int32 rtype=2;//房间模式 1classic 2=Quick straig
        required int64 betmin=3;//赌注范围下限
        required int64 betmax=4;//赌注范围上限
        required int32 maxplayers=5;//需参与人数
        required int32 roomlv=6;//房间等级
        required int64 minchips=7;//chips下限
        required int64 maxchips=8;//chips上限
        required double rtp=9;//rtp
        required int32 ginbonus=10;//ginbonus
        required int32 undercutbonus=11;//undercutbonus
    }
}

//----CMD ginmatch 匹配房间
message C2S_Gin_Match{
    required int32 gtype=1;//gin模式 1普通 2=Straight 3=Oklahoma 4=Hollywood 5=ThreeGin 6=Rummy500
    required int32 rtype=2;//房间模式 1classic 2=Quick straight 3=Private 4=Tournament
    required bool manual=3;//是否手动创建的房间
    required int32 score=4;//manual为false时随机一个值范围内的值
    required int32 bet=5;//manual为false时随机一个值范围内的值
    required int32 roomLv=6;//房间等级
}
//----CMD matchtimeout 超时
//----CMD ginmatchcancel 取消匹配房间


