//---CMD exhangelist
message S2C_Diamond_Exchanges {
    repeated S2C_Diamond_Exchange list = 1;
    message S2C_Diamond_Exchange{
        required int32 id = 1; //id
        required int32 amount = 2; //diamond 数量
        required int32 time = 3; // private 天数/autosort天数
        required int32 type = 4; // 1=private 2=autosort
    }
}


