//---CMD collectiongrouplist
message S2C_Collection_Groups{
    required double unlockrate = 2; //
    repeated S2C_Collection_Group groups = 1;
    message S2C_Collection_Group{
        required int32 id = 1; //id
        required string name = 2; //名字
        required int32 needs = 3; //需要的数量
        required int32 rewardchip = 4; //收集完成的奖励
        required int32 rewarddiamand = 5; //收集完成的奖励
        required int32 rewardscenes = 6; //收集完成的奖励
        required int32 rewardcard = 7; //收集完成的奖励
        required int32 rewardautosort = 10; //收集完成的奖励
        required int32 rewardprivate = 11; //收集完成的奖励
        required bool collected = 8; //玩家已合成
