    }
}

//---CMD collectionsrate
message S2C_CollectionRates{
    repeated S2C_LevelCollRates collrates = 1;

    message S2C_LevelCollRates{
        required int32 level = 1; //等级
        repeated S2C_StartRates weight = 2; //概率
    }


}

message S2C_StartRates{
    required int32 star = 1; //等级
    required int32 weight = 2; //概率
}


//---CMD collectionlist
//Common_I GROUPID
message S2C_Collections{
    required int32 id = 1;//GROUPID
    repeated S2C_Collection cols = 2;
    message S2C_Collection{
