package proto;

option optimize_for = LITE_RUNTIME;
option java_package = "com.aemobile.gin.common.bean";
option java_outer_classname = "GinProto";

//=================================================================================================
//Sfstranslate
message Sfstranslate {
    message Sfstable {
        required string key = 1; //key
        required int32 style = 2; //类型
        optional int32 index = 3; // 返回序号
    }
    repeated Sfstable tbl = 1;
    repeated bool isbool = 2;
    repeated int32 isint = 3;
    repeated string isstr = 4;
    repeated string isobj = 5;
    repeated double isdouble = 6;
}

//=================================================================================================
//tracking
message C2STrack {
    repeated Track track = 1; //跟踪数据
    message Track{
        required int32 trackid = 1; //跟踪点编号
    }
}

//common_replace_to_other_file_start========================================================

// 通用：int
message Common_I {
    optional int32 i = 1;
}

// 通用：long
message Common_L {
    optional int64 i = 1;
}

// 通用：String
message Common_S {
    required string i = 1;
}

// 通用：boolean
message Common_Boolean {
    required bool i = 1;
}

// 通用：int 列表
message Common_IS {
    repeated int32 is = 1;
}

// 通用：long 列表
message Common_LS {
    repeated int64 is = 1;
}
// 通用：boolean 列表
message Common_Booleans{
    repeated bool i = 1;
}

// 通用：String 列表
message Common_SS {
    repeated string is = 1;
}

// 通用：float
message Common_F {
    required float f = 1;
}

// 通用：message 用户success/error
message S2C_Common_Message {
    required string mess = 1;
    required string stage = 2;
    optional int32 errorcode = 3;
    optional int32 time = 4;
}
//common_replace_to_other_file_end========================================================

// 通用：用户Chips 修改
message S2C_Common_Change {
    required int64 change = 1; //变化数量
    required int64 final = 2; //最终数量
}

// 通用：ExpBar
message S2C_Common_ExpBar {
    optional int32 exp1 = 1; //当前等级用户经验
    optional int32 exp2 = 2; //当前等级升级总经验
    required int32 level = 3; //用户等级
}

// 通用：UserBase
//------------CMD getUserInfo
message S2C_Common_UserInfo {
    required int64 id = 1; //用户id
    required string name = 2;//用户名
    required string icon = 3;//头像 -1=不使用系统头像
    required string facebookID = 4;//facebook ID（仅facebook用户头像）
    required int64 chips=5; //chips
    required int64 diamonds=19; //chips
    required int32 level=6; //等级
    optional int32 friendsCount=7; //好友数
    optional int32 exp1 = 8; //自己:当前等级用户经验
    optional int32 exp2 = 9; //自己:当前等级升级总经验
    optional string country = 10;//国家二位代码
    optional int32 handsNum=11; //handsNum
    optional int32 ginNum=12; //ginNum
    optional int32 knockNum = 13;//knockNum
    optional int32 undercutNum = 14;//undercutNum
    optional int64 maxWin = 15;//maxWin
    optional int32 handsWin = 16; //handsWin
    optional int32 giftReceived=17; //giftReceived
    optional int32 collectionCompleted = 18;//collectionCompleted
    optional int32 privateexp = 20;//自己:privateexp
    optional int32 autosortexp = 22;//自己:autosortexp
    optional int32 lastlevel = 21;//自己:升级奖励之前的level
    optional int32 createDate = 23;//注册日期
    optional int32 namechange = 24; //名字修改需要的diamonds
    optional int32 onlinetime = 25; //在线时长
    optional int32 bonusforot = 26; //已领取在线时长奖励id
    optional int32 vip = 27; //VIP
    optional int32 vip1 = 28; //自己:当前等级付费
    optional int32 vip2 = 29; //自己:当前等级升级总付费
    optional bool noadds = 30; //去广告
    optional int32 showcardsexp = 31; //记牌器
}

message S2C_Base_UserInfo {
    required int64 id = 1; //用户id
    required string name = 2;//用户名
    required string icon = 3;//头像 -1=不使用系统头像
    optional string newicon = 4; //新的icon，如果没有，使用旧icon
    required string facebookID = 5;//facebook ID（仅facebook用户头像）
    optional int64 chips=6; //chips
    optional int64 diamonds=7; //chips
    optional int32 vip = 8; //VIP
}

message S2C_Fri_UserInfo{
    required int64 id = 1; //用户id
    required string name = 2;//用户名
    required string icon = 3;//头像 -1=不使用系统头像
    required string facebookID = 4;//facebook ID（仅facebook用户头像）
    required int64 chipsWin = 5;
    required int32 handsWin = 6;
    required int32 relation = 7;//1：fb好友 2：游戏好友
    required int64 chips = 8;
    optional int32 level=9; //等级
    optional bool sendchip=10; //当天是否赠送过chip
}

message S2C_Common_LevelBonus{
    required int32 level = 1 ;//特定奖励等级
    required int64 bonus = 2;//特定等级奖励
}

//--------------CMD  taskcompleted 主动
message S2C_Task_Completed{
    required int32 order = 1;//第几个任务
    required int32 type = 2;//任务类型
}
