package proto;

option optimize_for = LITE_RUNTIME;
option java_package = "com.aemobile.patti.room.bean";
option java_outer_classname = "PokerRoomProto";

import "roomproto.proto";
import "commonproto.proto";


//Room Variable
//ba: 大盲注
//mc: 最小带入
//User Variable


//------pokerstatus
message S2C_Poker_Status{
    message Player{
        required int32 playerId = 1;//玩家id
        required int64 chips = 2;//剩余的chips
        required bool isIngame = 3;//玩家是否参与当局游戏
        required bool isFold = 4;//玩家是否弃牌
	required int64 totalbet = 5; //玩家总下注
        required int32 status = 6;//玩家状态 WAIT(0), SBLIND(1), BBLIND(2), CALL(3), CHCECK(4), RAISE(5), ALLIN(6), FOLD(7),
    }
    repeated Player ps = 1; //玩家列表
    required bool isStart= 2;//玩家是否开始
    required int32 roundId = 3;//当前局的Id
    optional int32 nextId = 4;//下一个操作者
    optional int64 remainTime = 5;//下一个操作等待时间 / 开始等待时间
    repeated int32 cards = 6;//公共牌
    repeated int64 pools = 7;//奖池
    optional int32 endId = 8;//最后操作者
    optional int32 banker= 9;//庄家
    optional int64 currentbet = 10; //当前下注额
    optional int64 currentraise = 11; //当前加注最小额
    repeated int32 usercards = 12; //玩家手牌
}

//CMD pokerstarttime
// Common_I  //还有几秒开始游戏


//------pokerstart
message S2C_Poker_Start{
    required int32 roundId = 1;//当前局的Id
    message Start_Player{
        required int32 playerId = 1;//玩家id
        required int64 chips = 2;///剩余的chips
        optional int32 bet = 3;//下注额(大小盲注)
    }
    repeated Start_Player ps = 2; //玩家列表
    repeated int32 cards = 3; //玩家手牌
}

//------pokercall
//无C2S
//根据实际下注返回 pokerraise 或者pokercall S2C_Poker_Ope
message S2C_Poker_Ope{
    required int32 roundId = 1;//当前局的Id
    required int32 playId = 2;//操作者
    optional int32 bet = 3;//下注额
    required int64 chips = 4;//剩余chips
    optional int64 nbet = 5;//下注额(新)
}

//------pokerraise
//C2S:Common_I 下注额(作废)
//C2S:Common_L 下注额(新)
//根据实际下注返回 pokerraise 或者pokercall S2C_Poker_Ope

//------pokerallin
//无C2S
//根据实际下注返回 pokerraise 或者pokercall S2C_Poker_Ope

//------pokerfold
//无C2S
//S2C_Poker_Ope 无下注额

//------pokerovertime
//无C2S
//无S2C

//------pokernext
//推送
message S2C_Poker_Next{
    required int32 nextId = 1;//下个开始用户的playerId
    required int32 waitTime = 2;//下个用户的操作等待时间
    required int32 roundId = 3;//当前局的Id
}


//------pokerperflop
//推送
message S2C_Poker_Perflop{
    repeated int32 cards = 1;//公共牌
    repeated int64 pools = 2;//奖池
}

//------pokerpool
//Common_LS pools 

//------pokercompare
//推送
message S2C_Poker_Compare{
    required int32 roundId = 1;//当前局的Id
    message Compare_Player{
        required int32 playerId = 1;//玩家id
        repeated int32 group= 2;//最大手牌（最后一位皇家同花顺=10;同花顺=9;四条=8;葫芦=7;同花=6;顺子=5;三条=4;两对=3;一对=2;单张=1）
	repeated int32 cards= 3;//手牌
	required bool isfold= 4;//玩家是否fold
    }
    repeated Compare_Player ps = 2; //所有玩家列表
}

//------pokerend
//推送
message S2C_Poker_End{
    required int32 roundId = 1;//当前局的Id
    message Win_Player{
        required int32 playerId = 1;//玩家id
        required int64 chips = 2;//剩余chips
        repeated int32 wins= 3;//每个奖池赢得的金额
	repeated int32 cards= 4;//手牌
	repeated int64 nwins= 5;//每个奖池赢得的金额(新)
    }
    repeated Win_Player ps = 2; //赢钱玩家列表
}

//------------CMD pokerexpchange主动
message S2C_Poker_Exp{
	required int32 exp = 1;
	required S2C_Common_ExpBar expbar = 2;
	optional S2C_Common_Change bonus = 3;
}


//------pokersetrebuy
//C2S:Common_B 是否自动买入
//S2C:Common_B 是否自动买入

//------pokerbuyin
//C2S:Common_IS 买入 chips, playerid
//S2C:Common_LS 买入 chips, userid

